// 1. Get text from AI
const aiText = items[0].json.choices[0].message.content;

// 2. Convert text to Binary (File) format
const binaryData = Buffer.from(aiText).toString('base64');

// 3. Return as file
return [
  {
    json: {
      success: true,
      message: "Article converted to file!"
    },
    binary: {
      data: {
        data: binaryData,
        mimeType: 'text/markdown',
        fileExtension: 'md',
        fileName: 'AI_Generated_Article.md'
      }
    }
  }
];
